define('subapp/article/article_sidebar_switch',[
    'libs/Class',
    'jquery'
],function(
    Class,
    $
){
    var ArticleSidebarSwitch = Class.extend({
        init: function(){
            console.log('article sidebar switch begin');
            this.initClickSwitch();
        },
        initClickSwitch:function(){
            $('.sidebar-switch-wrapper').click(this.handleClickSwitch.bind(this));
        },
        handleClickSwitch:function(){
            if ($('.sidebar-switch.open-switch').css('display') == 'none') {
                this.hiddenSideBar();
            } else {
                this.showSidebar();
            }
        },
        hiddenSideBar:function(){
            $('#detail_content_right').removeClass('content-right-show');
            $('#detail_content_right').addClass('content-right-hidden');
            $('.main-article-control').css({
                                             'transform':'translateX('+this.getArticleMoveDistance()+'px)',
                                             '-webkit-transform':'translateX('+this.getArticleMoveDistance()+'px)'
            });
            $('.bottom-article-share').parent('.col-xs-12').css('transform','translateX('+this.getArticleMoveDistance()+'px)');
            $('.sidebar-switch.close-switch').hide();
            $('.sidebar-switch.open-switch').show();
        },
        showSidebar:function(){
            $('#detail_content_right').removeClass('content-right-hidden');
            $('#detail_content_right').addClass('content-right-show');
            $('.main-article-control').css({
                                            'transform':'translateX(0)',
                                            '-webkit-transform':'translateX(0)'
            });
            $('.bottom-article-share').parent('.col-xs-12').css('transform','translateX(0)');
            $('.sidebar-switch.close-switch').show();
            $('.sidebar-switch.open-switch').hide();
        },
        getArticleMoveDistance:function(){
            return this.getSidebarWidth()/2;
        },
        getSidebarWidth:function(){
            return $('#detail_content_right').innerWidth();
        }
    });
    return  ArticleSidebarSwitch;
});

define('subapp/user_follow',['libs/Class','jquery', 'subapp/account'], function(Class,$,AccountApp){
    var UserFollow = Class.extend({
        init: function () {
            this.$follow = $(".follow");
            this.$follow.on('click', this.handleFollow.bind(this));
        },

        getAccountApp:function(){
            this.AccountApp = this.AccountApp || new AccountApp();
            return this.AccountApp;
        },

        handleFollow: function (e) {
            var that = this;
            var $followButton = $(e.currentTarget);
            var uid = $followButton.attr('data-user-id');
            var status = $followButton.attr('data-status');
            var action_url = "/u/" + uid;

            if (status == 1) {
                action_url += "/unfollow/";

            } else {
                action_url += "/follow/";
            }

            $.when($.ajax({
                url: action_url,
                dataType: 'json',
                method: 'POST'
            })).then(function success(data) {
                console.log('success');
                console.log(data);
                if (data.status == 1) {
                    $followButton.html('<i class="fa fa-check fa-lg"></i>&nbsp; 已关注');
                    $followButton.attr('data-status', '1');
                    $followButton.removeClass("button-blue").addClass("btn-cancel");
                    $followButton.removeClass("newest-button-blue").addClass("new-btn-cancel");

                } else if (data.status == 2) {
                    console.log('mutual !!!');
                    $followButton.html('<i class="fa fa-exchange fa-lg"></i>&nbsp; 已关注');
                    $followButton.removeClass('button-blue').addClass('btn-cancel');
                    $followButton.removeClass("newest-button-blue").addClass("new-btn-cancel");
                    $followButton.attr('data-status', '1');

                } else if (data.status == 0) {
                    $followButton.html('<i class="fa fa-plus"></i>&nbsp; 关注');
                    $followButton.removeClass("btn-cancel").addClass("button-blue");
                    $followButton.removeClass("new-btn-cancel").addClass("newest-button-blue");
                    $followButton.attr('data-status', '0');
                } else {
                    console.log('did not response with valid data');
                }
            }, function fail(error) {
                console.log('failed' + error);
                var html = $(error.responseText);
                that.getAccountApp().modalSignIn(html);
            });
        }
    });
    return UserFollow;
});

